class GameOfLife final: public GameTemplate
{
public:
	GameOfLife(const int scale = 3);
	~GameOfLife() override;

	GameOfLife(const GameOfLife& other) = delete;
	GameOfLife(GameOfLife&& other) noexcept = delete;
	GameOfLife& operator=(const GameOfLife& other) = delete;
	GameOfLife& operator=(GameOfLife&& other) noexcept = delete;

	void HandleEvents(const SDL_Event& event) override;
	void Update() override;
	void Resize(int new_width, int new_height);
	void Render() override;
private:
	static void RenderPixel(int x, int y);
	bool IsAlive(int x, int y) const;
	TMatrix<bool>* m_pGrid;
	TMatrix<bool>* m_pBuffer;
	const int m_Scale = 1;
};

void GameOfLife::Update() {
  const int width = m_pGrid->Rows(), height = m_pGrid->Cols();
  for (int x = 0; x < width; x++) {
    for (int y = 0; y < height; y++) {
      m_pBuffer->Get(x, y) = IsAlive(x, y);
    }
  }
  for (int x = 0; x < width; x++) {
    for (int y = 0; y < height; y++) {
      m_pGrid->Get(x, y) = m_pBuffer->Get(x, y);
    }
  }
}

template<typename T>
class TMatrix final {
  public:
    TMatrix(int rows, int cols);
    ~TMatrix();
    T& Get(int x, int y) { return m_pGrid[x][y]; }
    int Rows() const { return m_Rows; }
    int Cols() const { return m_Cols; }
  private:
    T** m_pGrid;
    int m_Rows, m_Cols;
};
